package tpn1;

public class Ejercicio2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		/*
		Realizá un programa en el que, siendo ​num1 ​y ​num2 dos números enteros con valor 4 
		 y 5 respectivamente,
		 muestre el resultado de la suma entre ambos y luego el producto entre ambos.
		 */
		
		int num1;
		int num2;
		num1=4;
		num2=5; 
		int suma;
		int multiplicacion; 
		suma=num1+num2;
		multiplicacion=num1*num2;
		
		
		System.out.println("La suma entre " + num1 + " y "+  num2  + " es " + suma);
		System.out.println("El producto entre ellos es " + multiplicacion);
	}

}
