package pruebas;

public class Pruebaconif {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num;
		num = 0;

		while (num <= 20) {
			if (num % 6 == 0) {
				System.out.println(num + " es multiplo de 6");
			}
			System.out.println(num);
			num = num + 2;

		}

	}

}
